    private void lbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_lbtnActionPerformed
        // TODO add your handling code here:
        try{ Class.forName("oracle.jdbc.driver.OracleDriver");
  Connection con=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:xe","system","system");
         PreparedStatement ps=con.prepareStatement("select * from student_info where aadharNo=?");
        ps.setInt(1,Integer.parseInt(atxt1.getText()));
         ResultSet rs=ps.executeQuery();
         if(rs.next()){
        // txt1.setText(rs.getString(1));
                          //txt1.setText(String.valueOf(rs.getString(1)));

         ntxt2.setText(rs.getString(1));
         
                 ftxt3.setText(rs.getString(2));
                atxt1.setText(String.valueOf(rs.getInt(3)));
                 ctxt4.setText(rs.getString(4));
                 //stxt5.setText(String.valueOf(rs.getInt(5)));
                         }
         else{
         JOptionPane.showMessageDialog(null,"Not Found Student");}
         con.close();}
         catch(Exception e){JOptionPane.showMessageDialog(this, e+" Error");}
    }//GEN-LAST:event_lbtnActionPerformed

