    private void submitbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitbtnActionPerformed
        // TODO add your handling code here:
        try{ Class.forName("oracle.jdbc.driver.OracleDriver");
            Connection con=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:xe","system","system");
            PreparedStatement ps=con.prepareStatement("insert into fee_deposite values(?,?,?,?,?,?,?,?,?,?,?,?,?)");
            ps.setString(1,atxt1.getText());
            ps.setString(2,ntxt2.getText());
            ps.setString(3,ftxt3.getText());
            ps.setString(4,ctxt4.getText());
            String semester=semcb.getSelectedItem().toString();
            ps.setString(5,semester);
            // int Fees=Integer.parseInt(ftxt6.getText());
            ps.setString(6,ftxt6.getText());
            if(cash.isSelected()){ ps.setString(7,cash.getText());}
            else{ps.setString(7,dd.getText());}
            ps.setString(8,fp.getText());
            ps.setString(9,an.getText());
            ps.setString(10,cdd.getText());
            ps.setString(11,ptxt7.getText());
            //  ps.setInt(11,Paid);

            ps.setString(12,btxt9.getText());
            //ps.setInt(9,Balance);
            // int Balance=Fees-Paid;
            //ps.setInt(9,Balance);
            ps.setString(13,dtxt8.getText());
            int a=ps.executeUpdate();
            JOptionPane.showMessageDialog(this,a+" Fees Successfully Deposited");
            con.close();}
        catch(Exception ex){JOptionPane.showMessageDialog(null, ex+"Connection Error");}
    }//GEN-LAST:event_submitbtnActionPerformed

